mapper.select:
	SELECT * FROM (
	    SELECT ROWNUM rnum, tb.* FROM (
	        SELECT num, subject, userName, hitCount, TO_CHAR(created, 'YYYY-MM-DD') created, saveFilename
	        FROM bbs b
	        JOIN member1 m1
	        ON b.userId = m1.userId
	        
				
					
				
			
	        ORDER BY num DESC
	    ) tb
 
	    WHERE ROWNUM <= #{end}
	) WHERE rnum >= #{start}

mapper.update:
UPDATE bbs SET
	hitCount = hitCount + 1
WHERE num = #{num}
